import React, { useState, useEffect } from 'react';
import '../../App.css';
import '../../malak.css';
import Navbar from '../../component/uniSuper/Navbar';
import CardReportSuper from '../../component/uniSuper/CardReportSuper';
import student from '../../static/Student.jpg';

function ReportUniSuper() {
  let [isModal, setIsModal] = useState(false);

  useEffect(() => {
    if (isModal) {
      // Attach event listener to handle clicks outside the modal
      document.body.addEventListener('click', handleOnClickAnywhere);

      // Cleanup the event listener when the component unmounts or modal is closed
      return () => {
        document.body.removeEventListener('click', handleOnClickAnywhere);
      };
    }
  }, [isModal]);

  let handelOnClick = () => {
    setIsModal(true);
  };

  let handelOnClickX = () => {
    setIsModal(false);
  };

  let handleOnClickAnywhere = (event) => {
    // Check if the click is outside the modal content
    if (!event.target.closest('.modal-content')) {
      setIsModal(false);
    }
  };

  return (
    <>
      <Navbar />
      <div className='centerd-comp'>
        <div className='uni-super-title-report'>
          <span className='font-super bold'>Students Report</span>
          <input
            type='text'
            placeholder='Search Student Report'
            className='uni-super-search-report gray-bk '
            onClick={handelOnClick}
          />
        </div>
        <CardReportSuper />
      </div>
      <div className={isModal ? 'show modal-content' : 'hidden'}>
        <div className='search-box-left-mini'>
          <div className='search-card gray-bk'>
            <div className='search-img'>
              <img src={student} className='uni-std-circle' />
              <div className='compsuper-std-name'>
                <span>Mohammad Saleh</span>
                <span>CS</span>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
}

export default ReportUniSuper;
